export default "precision mediump float;\n#define GLSLIFY 1\nuniform float u_time;uniform vec2 u_resolution;uniform float u_scroll;const vec3 purple=vec3(0.345,0.11,0.529);const vec3 blue=vec3(0.118,0.227,0.541);void main(){vec2 screenspace=gl_FragCoord.xy/u_resolution.xy;float wave1=sin(screenspace.x*10.0+u_scroll/600.0+u_time/3000.0)/8.0+0.75;float wave2=sin(screenspace.x*6.5+u_scroll/400.0+u_time/2000.0)/8.0+0.5;float wave3=sin(screenspace.x*3.0+u_scroll/200.0+u_time/1000.0)/8.0+0.25;if(screenspace.y<wave3){gl_FragColor=vec4(mix(purple,blue,0.30),1.0);}else if(screenspace.y<wave2){gl_FragColor=vec4(mix(purple,blue,0.45),1.0);}else if(screenspace.y<wave1){gl_FragColor=vec4(mix(purple,blue,0.70),1.0);}else{gl_FragColor=vec4(blue,1.0);}}"